catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading) return;

  var field = 'cpr_requester_email';
  var email = (newValue || '').trim();

  // ล้างข้อความเตือนเดิม
  g_form.hideFieldMsg(field, true);

  // ถ้าว่าง ให้ mandatory จัดการเอง
  if (!email) return;

  // ❌ ไม่อนุญาตอักขระที่ไม่ใช่ภาษาอังกฤษ
  // อนุญาตเฉพาะ a-z A-Z 0-9 . _ - @
  var englishOnly = /^[A-Za-z0-9._@-]+$/;

  if (!englishOnly.test(email)) {
    g_form.showFieldMsg(
      field,
      'กรุณาใช้อักษรภาษาอังกฤษเท่านั้น (a-z, A-Z, 0-9) และสัญลักษณ์ . _ - @',
      'error'
    );
    return;
  }

  // ✅ ตรวจรูปแบบอีเมล
  var emailFormat = /^[A-Za-z0-9._-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;

  if (!emailFormat.test(email)) {
    g_form.showFieldMsg(
      field,
      'รูปแบบอีเมลไม่ถูกต้อง กรุณากรอกในรูปแบบ name@example.com',
      'error'
    );
    return;
  }
}
