sys_script.script:
(function executeRule(current, previous) {

    if (!current.cpr_start_datetime || !current.cpr_end_datetime) {
        return;
    }

    var start = new GlideDateTime(current.cpr_start_datetime);
    var end   = new GlideDateTime(current.cpr_end_datetime);

    if (end.getNumericValue() <= start.getNumericValue()) {
        gs.addErrorMessage('End Date/Time ต้องมากกว่า Start Date/Time');
        current.setAbortAction(true);
        return;
    }

    // ฟังก์ชันคิดเงิน "ต่อวัน"
    function calcFeeForDay(hours) {
        if (hours <= 0) return 0;
        if (hours > 8) return 200;

        var fee = 10; // ชั่วโมงแรก

        if (hours > 1) {
            fee += (Math.min(hours, 4) - 1) * 15;
        }
        if (hours > 4) {
            fee += (Math.min(hours, 8) - 4) * 20;
        }
        return fee;
    }

    var totalFee = 0;
    var totalHours = 0;

    var cur = new GlideDateTime(start);

    while (cur.getNumericValue() < end.getNumericValue()) {

        // สิ้นสุดของวันนั้น (23:59:59)
        var endOfDay = new GlideDateTime(cur);
        endOfDay.setDisplayValue(
            cur.getLocalDate().getByFormat('yyyy-MM-dd') + ' 23:59:59'
        );

        var sliceEnd = endOfDay;
        if (endOfDay.getNumericValue() > end.getNumericValue()) {
            sliceEnd = end;
        }

        var diffMin = (sliceEnd.getNumericValue() - cur.getNumericValue()) / (1000 * 60);
        var hours = Math.ceil(diffMin / 60);

        totalHours += hours;
        totalFee   += calcFeeForDay(hours);

        // ขยับไปวันถัดไป 00:00
        cur = new GlideDateTime(sliceEnd);
        cur.addSeconds(1);
    }

    current.cpr_duration_hours = totalHours;
    current.cpr_total_fee = totalFee;

})(current, previous);
