sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // ถ้าไม่มีทะเบียนหรือเวลา ไม่ต้องเช็ก
    if (!current.cpr_vehicle_reg_no || !current.cpr_start_datetime || !current.cpr_end_datetime) {
        return;
    }

    // กันกรณี End <= Start
    var start = new GlideDateTime(current.cpr_start_datetime);
    var end   = new GlideDateTime(current.cpr_end_datetime);

    if (end.getNumericValue() <= start.getNumericValue()) {
        gs.addErrorMessage('End Date/Time ต้องมากกว่า Start Date/Time');
        current.setAbortAction(true);
        return;
    }

    // ใช้ table เดียวกับ record นี้ (V2)
    var gr = new GlideRecord(current.getTableName());

    // รถทะเบียนเดียวกัน
    gr.addQuery('cpr_vehicle_reg_no', current.cpr_vehicle_reg_no);

    // ไม่เอา record ตัวเองเวลา update
    gr.addQuery('sys_id', '!=', current.sys_id);

    // *** เช็กเฉพาะคนละ Location ตาม requirement ข้อ 3 ***
    gr.addQuery('cpr_location', '!=', current.cpr_location);

    // ถ้าอยากให้ "ห้ามจองซ้ำไม่ว่าที่ไหน" ให้ลบบรรทัดด้านบนออก

    // เงื่อนไขช่วงเวลาทับซ้อน: existing.start < new.end AND existing.end > new.start
    gr.addQuery('cpr_start_datetime', '<', current.cpr_end_datetime);
    gr.addQuery('cpr_end_datetime', '>', current.cpr_start_datetime);

    gr.query();

    if (gr.next()) {
        gs.addErrorMessage('ไม่สามารถจองได้: รถทะเบียนนี้มีการจองในช่วงเวลานี้อยู่แล้วที่ Location อื่น');
        current.setAbortAction(true);
    }

})(current, previous);
