sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // ถ้า state ไม่ได้เปลี่ยน ไม่ต้องทำอะไร
    if (!current.state.changes()) {
        return;
    }

    // ดึง record ต้นฉบับ (Car Parking Reservation V2)
    var task = current.sysapproval.getRefRecord();
    if (!task || !task.isValidRecord()) {
        return;
    }

    // กันเผื่อ approval ของ table อื่น
    if (task.getTableName() != 'x_1869632_car_pa_0_car_parking_reservation_v2') {
        return;
    }

    // ดึงคอมเมนต์ล่าสุดจาก Approval (journal)
    var lastComment = '';
    try {
        // getJournalEntry(-1) = entry ล่าสุด
        lastComment = current.comments.getJournalEntry(-1) || '';
    } catch (e) {
        // กัน error ไว้เฉย ๆ
        lastComment = '';
    }

    // --------------------------
    //   เมื่อ Approved
    // --------------------------
    if (current.state.changesTo('approved')) {

        // อัปเดต Assigned To = ผู้อนุมัติ
        task.cpr_assigned_to = current.approver;

        // อัปเดตสถานะ
        task.cpr_status = 'confirmed';

        // บันทึกโน้ตของ Staff ถ้ามี
        if (lastComment) {
            task.cpr_staff_notes = lastComment;
        }

        task.update();
        return;
    }

    // --------------------------
    //   เมื่อ Rejected
    // --------------------------
    if (current.state.changesTo('rejected')) {

        // อัปเดตสถานะ
        task.cpr_status = 'rejected';

        // บันทึกเหตุผล Reject
        if (lastComment) {
            task.cpr_reject_reason = lastComment;   // ฟิลด์ Reject Reason บน Car Parking Reservation V2
        }

        // ให้ Assigned To เป็นคนที่กด Reject ด้วย
        task.cpr_assigned_to = current.approver;

        task.update();
        return;
    }

})(current, previous);
