sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var startStr = g_form.getValue('cpr_start_datetime');
    var endStr = g_form.getValue('cpr_end_datetime');

    if (startStr && endStr) {
        var start = new Date(startStr);
        var end = new Date(endStr);

        if (end <= start) {
            g_form.showFieldMsg('cpr_end_datetime', 'End Date ต้องมากกว่า Start Date', 'error');
            return;
        }

        function calcFeeForDay(hours) {
            if (hours <= 0) return 0;
            if (hours > 8) return 200;
            var fee = 10;
            if (hours > 1) fee += (Math.min(hours, 4) - 1) * 15;
            if (hours > 4) fee += (Math.min(hours, 8) - 4) * 20;
            return fee;
        }

        var totalFee = 0;
        var totalHours = 0;
        // เปลี่ยนจาก current เป็น checkDate เพื่อแก้ Error
        var checkDate = new Date(start); 

        while (checkDate < end) {
            var endOfDay = new Date(checkDate);
            endOfDay.setHours(23, 59, 59, 999);

            var sliceEnd = endOfDay < end ? endOfDay : end;
            var diffMin = (sliceEnd - checkDate) / (1000 * 60);
            var hoursInSlice = Math.ceil(diffMin / 60);

            totalHours += hoursInSlice;
            totalFee += calcFeeForDay(hoursInSlice);

            checkDate = new Date(sliceEnd);
            checkDate.setSeconds(checkDate.getSeconds() + 1);
        }

        g_form.setValue('cpr_duration_hours', totalHours);
        g_form.setValue('cpr_total_fee', totalFee);
    }
}
