sys_script_client.script:
function onSubmit() {
    var field = 'cpr_start_datetime';
    var value = g_form.getValue(field);

    g_form.hideFieldMsg(field, true);

    if (!value) return true;

    // ดึงเวลาปัจจุบัน (Client Side)
    var now = new Date();

    // แปลงค่าจากฟิลด์เป็น Date Object 
    // สำหรับหน้าฟอร์มปกติ แนะนำให้ใช้ getDateFromFormat ของ ServiceNow ถ้ามี Format ที่แน่นอน
    var start = new Date(value);

    if (start < now) {
        g_form.showFieldMsg(
            field,
            'ไม่สามารถจองวัน/เวลาย้อนหลังจากเวลาปัจจุบันได้',
            'error'
        );

        // เลื่อนหน้าจอไปที่ฟิลด์ที่มีปัญหาเพื่อให้ผู้ใช้เห็นข้อความชัดเจน
        g_form.getControl(field).focus(); 

        return false; // ❌ ระงับการบันทึก (Submit/Update)
    }

    return true;
}
