sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        // ล้างข้อมูลถ้าช่องชื่อว่างเปล่า
        g_form.setValue('cpr_requester_phone', '');
        g_form.setValue('cpr_requester_email', '');
        return;
    }

    // ดึงเรคคอร์ดของ User ที่ถูกเลือกมา
    var user = g_form.getReference('cpr_requester_name', function(userRecord) {
        // ใส่ค่า Phone และ Email ที่ดึงมาได้ลงในฟิลด์
        // ในตาราง sys_user มาตรฐานจะใช้ฟิลด์ 'mobile_phone' หรือ 'phone' และ 'email'
        g_form.setValue('cpr_requester_phone', userRecord.mobile_phone || userRecord.phone); 
        g_form.setValue('cpr_requester_email', userRecord.email);
    });
}
