sys_script_client.script:
function onSubmit() {
    var startField = 'cpr_start_datetime';
    var endField = 'cpr_end_datetime';

    var startVal = g_form.getValue(startField);
    var endVal = g_form.getValue(endField);

    g_form.hideFieldMsg(startField, true);
    g_form.hideFieldMsg(endField, true);

    var now = new Date();
    var startDate = new Date(startVal);
    var endDate = new Date(endVal);

    // 1. เช็ควันเริ่มห้ามย้อนหลัง
    if (startVal && startDate < now) {
        g_form.showFieldMsg(startField, 'ไม่สามารถจองวัน/เวลาย้อนหลังได้', 'error');
        g_form.getControl(startField).focus();
        return false;
    }

    // 2. เช็ควันสิ้นสุดห้ามก่อนวันเริ่ม
    if (startVal && endVal && endDate <= startDate) {
        g_form.showFieldMsg(endField, 'วันสิ้นสุดต้องอยู่หลังวันเริ่มต้นเสมอ', 'error');
        g_form.getControl(endField).focus();
        return false;
    }

    return true;
}
